import { useState, useCallback, useEffect } from 'react';
import { ethers } from 'ethers';

// ABI for our ZKCoProcessor contract
const ZKCoProcessorABI = [
  "event ComputationRequested(bytes32 indexed taskId, address requester)",
  "event ProofSubmitted(bytes32 indexed taskId, address operator)",
  "event ComparisonLogged(uint256 zkGasUsed, uint256 normalGasUsed, uint256 timeSaved)",
  "event RewardPaid(address indexed operator, uint256 amount, bytes32 indexed taskId)",
  "function requestComputation(bytes calldata inputData) external payable",
  "function submitProof(bytes32 taskId, bytes calldata proof) external",
  "function logComparison(uint256 zkGasUsed, uint256 normalGasUsed, uint256 timeSaved) external",
  "function tasks(bytes32) external view returns (bool completed, uint256 startTime, uint256 endTime, uint256 reward, address operator, uint256 complexity)",
  "function totalTasksProcessed() external view returns (uint256)"
];

export const useZKVerifier = () => {
  const [contract, setContract] = useState(null);
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  // Store recent comparisons
  const [recentComparisons, setRecentComparisons] = useState([]);

  // Placeholder for contract address - should be configured based on deployment
  const contractAddress = process.env.REACT_APP_CONTRACT_ADDRESS || "0x1234567890123456789012345678901234567890"; // Replace with actual address after deployment

  // Initialize ethers and connect to the contract
  useEffect(() => {
    const init = async () => {
      // Check if window.ethereum is available (MetaMask)
      if (window.ethereum) {
        try {
          // Request account access
          const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });

          // Create a Web3Provider
          const web3Provider = new ethers.providers.Web3Provider(window.ethereum);
          setProvider(web3Provider);

          // Get the signer
          const web3Signer = web3Provider.getSigner();
          setSigner(web3Signer);

          // Set account
          setAccount(accounts[0]);

          // Create contract instance
          const zkContract = new ethers.Contract(contractAddress, ZKCoProcessorABI, web3Signer);
          setContract(zkContract);

          setIsConnected(true);

          // Set up event listener for comparison logging
          zkContract.on("ComparisonLogged", (zkGasUsed, normalGasUsed, timeSaved) => {
            const newComparison = {
              zkGasUsed: zkGasUsed.toNumber(),
              normalGasUsed: normalGasUsed.toNumber(),
              timeSaved: timeSaved.toNumber()
            };

            setRecentComparisons(prev => [newComparison, ...prev.slice(0, 9)]);
          });

        } catch (error) {
          console.error("Error initializing Web3:", error);
        }
      } else {
        console.error("Ethereum provider not found. Please install MetaMask.");
      }
    };

    init();

    // Cleanup event listeners when component unmounts
    return () => {
      if (contract) {
        contract.removeAllListeners();
      }
    };
  }, [contractAddress]);

  // Request computation function
  const requestComputation = useCallback(async (inputData) => {
    if (!contract || !signer) {
      throw new Error("Web3 not initialized");
    }

    try {
      // Convert input data to bytes
      const inputBytes = ethers.utils.toUtf8Bytes(inputData);

      // Estimate the cost (task complexity based on data size)
      const complexity = inputBytes.length;
      const baseReward = ethers.utils.parseEther("0.001");
      const estimatedReward = baseReward.mul(ethers.BigNumber.from(1 + Math.floor(complexity / 100)));

      // Send transaction with the reward amount
      const tx = await contract.requestComputation(inputBytes, { value: estimatedReward });
      await tx.wait();

      return tx;
    } catch (error) {
      console.error("Error requesting computation:", error);
      throw error;
    }
  }, [contract, signer]);

  // Fetch comparison data for visualization
  const fetchComparisonData = useCallback(async () => {
    // In a real implementation, you would fetch this from the contract events
    // For demo purposes, we'll generate some sample data
    // In production, you would query the ComparisonLogged events

    if (!contract) {
      throw new Error("Web3 not initialized");
    }

    // Simulating event query - in real implementation, you would filter events
    const mockData = {
      zkGasUsed: Math.floor(Math.random() * 50000) + 10000,
      normalGasUsed: Math.floor(Math.random() * 100000) + 70000,
      timeSaved: Math.floor(Math.random() * 500) + 100
    };

    return mockData;
  }, [contract]);

  // Get total tasks processed
  const getTotalTasksProcessed = useCallback(async () => {
    if (!contract) {
      throw new Error("Web3 not initialized");
    }

    try {
      const total = await contract.totalTasksProcessed();
      return total.toNumber();
    } catch (error) {
      console.error("Error getting total tasks:", error);
      return 0;
    }
  }, [contract]);

  // Get latest comparisons
  const getLatestComparisons = useCallback(() => {
    // Return the stored recent comparisons
    return recentComparisons;
  }, [recentComparisons]);

  return {
    requestComputation,
    fetchComparisonData,
    getTotalTasksProcessed,
    getLatestComparisons,
    isConnected,
    account
  };
};
