import React, { useState, useEffect } from 'react';
import { useZKVerifier } from '../hooks/useZKVerifier';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const App = () => {
  const { 
    requestComputation, 
    fetchComparisonData, 
    getTotalTasksProcessed,
    getLatestComparisons
  } = useZKVerifier();

  const [comparisonData, setComparisonData] = useState(null);
  const [totalTasks, setTotalTasks] = useState(0);
  const [inputData, setInputData] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [comparisons, setComparisons] = useState([]);

  useEffect(() => {
    // Fetch initial data
    const fetchInitialData = async () => {
      try {
        const tasks = await getTotalTasksProcessed();
        setTotalTasks(tasks);

        const latestComparisons = await getLatestComparisons();
        setComparisons(latestComparisons);
      } catch (error) {
        console.error('Error fetching initial data:', error);
      }
    };

    fetchInitialData();
  }, [getTotalTasksProcessed, getLatestComparisons]);

  const handleComputationRequest = async () => {
    if (!inputData) {
      alert('Please enter some input data');
      return;
    }

    try {
      setIsLoading(true);
      // Send computation request to ZKCoProcessor smart contract
      await requestComputation(inputData);

      // Fetch updated data
      const tasks = await getTotalTasksProcessed();
      setTotalTasks(tasks);

      // Fetch comparison data for visualization
      const data = await fetchComparisonData();
      setComparisonData(data);

      // Fetch latest comparisons
      const latestComparisons = await getLatestComparisons();
      setComparisons(latestComparisons);

      setInputData('');
    } catch (error) {
      console.error('Error:', error);
      alert('Error processing computation request');
    } finally {
      setIsLoading(false);
    }
  };

  // Chart data configuration
  const chartData = {
    labels: ['Gas Used (ZK Process)', 'Gas Used (Normal Process)'],
    datasets: [
      {
        label: 'Gas Usage Comparison',
        data: comparisonData ? [comparisonData.zkGasUsed, comparisonData.normalGasUsed] : [0, 0],
        backgroundColor: ['rgba(54, 162, 235, 0.6)', 'rgba(255, 99, 132, 0.6)'],
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Gas Usage Comparison: ZK vs Normal Processing',
      },
    },
  };

  return (
    <div className="dashboard">
      <h1>ZK Co-Processor Dashboard</h1>

      <div className="stats-container">
        <div className="stat-card">
          <h3>Total Tasks Processed</h3>
          <p className="stat-value">{totalTasks}</p>
        </div>

        {comparisonData && (
          <div className="stat-card">
            <h3>Time Saved</h3>
            <p className="stat-value">{comparisonData.timeSaved} ms</p>
          </div>
        )}

        {comparisonData && (
          <div className="stat-card">
            <h3>Gas Savings</h3>
            <p className="stat-value">
              {comparisonData.normalGasUsed - comparisonData.zkGasUsed} gas
              ({Math.round(((comparisonData.normalGasUsed - comparisonData.zkGasUsed) / comparisonData.normalGasUsed) * 100)}%)
            </p>
          </div>
        )}
      </div>

      <div className="chart-container">
        {comparisonData && <Bar data={chartData} options={chartOptions} />}
      </div>

      <div className="computation-form">
        <h2>Request New Computation</h2>
        <textarea
          value={inputData}
          onChange={(e) => setInputData(e.target.value)}
          placeholder="Enter data for computation..."
          rows={4}
        />
        <button 
          onClick={handleComputationRequest}
          disabled={isLoading}
          className="request-button"
        >
          {isLoading ? 'Processing...' : 'Request Computation'}
        </button>
      </div>

      <div className="recent-comparisons">
        <h2>Recent Comparisons</h2>
        <table>
          <thead>
            <tr>
              <th>ZK Gas Used</th>
              <th>Normal Gas Used</th>
              <th>Time Saved (ms)</th>
              <th>Efficiency Gain</th>
            </tr>
          </thead>
          <tbody>
            {comparisons.map((comparison, index) => (
              <tr key={index}>
                <td>{comparison.zkGasUsed}</td>
                <td>{comparison.normalGasUsed}</td>
                <td>{comparison.timeSaved}</td>
                <td>
                  {Math.round(((comparison.normalGasUsed - comparison.zkGasUsed) / comparison.normalGasUsed) * 100)}%
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
